import React from "react";
import { StyleSheet, View, Text, Button, ImageBackground } from 'react-native';
import { TextInput } from "react-native-gesture-handler";

const backgroundImage = require("../assets/Background-Image.png");

export default class Start extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: ""
    };
  }

  render() {
    return (
      <View style={styles.container}>
        <ImageBackground style={styles.backgroundImage} source={require('../assets/Background-Image.png')}>
          <Text style={styles.AppTitle}>App Title</Text>

          <View style={styles.card}>
            <TextInput
              style={{ height: 40, borderColor: "gray", borderWidth: 1 }}
              value={this.state.name}
              onChangeText={(name) => this.setState({ name: name })}
              placeholder="Your name"
            >
            </TextInput>

            <Text style={styles.chooseTitle}>Choose Background Color:</Text>
            <View style={styles.containerColors}>
              <View style={styles.first}></View>
              <View style={styles.second}></View>
              <View style={styles.third}></View>
              <View style={styles.fourth}></View>
            </View>

            <Button
              style={styles.chatButton}
              title="Start Chatting"
              onPress={() => this.props.navigation.navigate('Chat', { name: this.state.name })}
            >
            </Button>
          </View>
        </ImageBackground>

      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  backgroundImage: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-evenly',
    alignItems: 'center',
    resizeMode: 'cover',
  },
  AppTitle: {
    fontSize: 45,
    weight: 600,
  },
  card: {
    height: '44%',
    width: '88%',
    backgroundColor: '#FFFFFF'
  },
  chooseTitle: {
    color: '#757083',
    fontSize: 16,
    fontWeight: '300'
  },

  chatButton: {
    fontSize: 16,
    fontVariant: 600,
    fontColor: '#FFFFFF',
    buttonColor: '757083',
  },
  containerColors: {
    flexDirection: 'row',
  },
  first: {
    flex: 0.3,
    backgroundColor: '#090C08',
    width: 100,
    height: 100,
    borderRadius: 100 / 2,
  },
  second: {
    flex: 0.3,
    backgroundColor: '#474056',
    width: 100,
    height: 100,
    borderRadius: 100 / 2,
  },
  third: {
    flex: 0.3,
    backgroundColor: '#8A95A5',
    width: 100,
    height: 100,
    borderRadius: 100 / 2,
  },
  fourth: {
    flex: 0.3,
    backgroundColor: '#B9C6AE',
    width: 100,
    height: 100,
    borderRadius: 100 / 2,
  }
});
